import styled from 'styled-components';

export const Container = styled.section`
  display: flex;
  flex-direction: column;

  margin-top: 3rem;
  padding: 0.3rem;
`;

export const Card = styled.div`
  display: flex;
  flex-direction: row;
  width: calc(100% - 2rem);
  height: auto;
  padding: 1rem;
  background: #f5f5f5;
  border-radius: 4px;
  margin-bottom: 0.5rem;
  justify-content: space-between;

  section {
    display: flex;
    flex-direction: row;
  }

`;
export const Info = styled.div`
  display: flex;
  flex-direction: column;
  padding-left: 1rem;
  span {
    font-size: 1rem;
  }
  div {
    margin: 0.3rem !important;
  }
`;
