import React, { memo, useState } from 'react';
import { Container } from './styles';

interface IncrementProps {
  // title: string;
  value: number;
  max?: number;
  min?: number;
  vertical?: boolean;
}

const Increment: React.FC<IncrementProps> = ({ value, max = 100, min = 0 }) => {
  const [state, setState] = useState(value.toString());

  function increment() {
    if (parseInt(state) < max) {
      value = parseInt(state) + 1;
      // setState(sum.toString());
    }
  }

  function decrement() {
    if (parseInt(state) > min) {
      let subtract = parseInt(state) - 1;
      setState(subtract.toString());
    }
  }

  return (
    <Container >
      <button type="button" onClick={() => increment()}>+</button>
      <p>{state}</p>
      <button type="button" onClick={() => decrement()}>-</button>
    </Container>
  );
}

export default memo(Increment)
