import React, { memo, useState } from 'react';
import { Container } from './styles';

interface IncrementProps {
  // title: string;
  state: number;
  setState: Function;
  initial?: number;
  max?: number;
  min?: number;
  vertical?: boolean;
}

const Increment: React.FC<IncrementProps> = ({ state, setState, initial, max = 100, min = 0 }) => {
  const [quantity, setQuantity] = useState(initial.toString());

  function increment() {
    if ((state) < max) {
      let sum = (state) + 1;
      setState(sum.toString());
    }
  }

  function decrement() {
    if ((state) > min) {
      let subtract = (state) - 1;
      setState(subtract.toString());
    }
  }

  return (
    <Container >
      <button type="button" onClick={() => increment()}>+</button>
      <p>{state}</p>
      <button type="button" onClick={() => decrement()}>-</button>
    </Container>
  );
}

export default memo(Increment)
