import Header from "@/components/Header";
import { useCart } from '@/context/Cart/CartContext';
import { ThumbnailSmall } from "@/styles/genericStyles";
import { Card, Container, Info } from "@/styles/pages/Cart/styles";
import PrismicDOM from 'prismic-dom';
import { useEffect } from "react";

export default function Cart() {
  const {cart, setCart} = useCart();

  useEffect(() => {
    console.log(cart);
  }, [cart])
  // setCart({ type: 'ADD', payload: product });
  // setCart({ type: 'REMOVE', payload: product.id });

  return (
    <>
      <Header />
      <Container>
        {cart.map(product => (
          <Card key={product.id}>
            <ThumbnailSmall src={product.data.thumbnail.url} alt=""/>
            <Info>
              <span>{PrismicDOM.RichText.asText(product.data.name)}</span>
            </Info>
          </Card>
        ))}
      </Container>
    </>
  );
}
