import { createGlobalStyle } from 'styled-components';

export default createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
  }

  body {
    background: #FFF;
    color: #000;
    font-family: 'Nunito', sans-serif;
    font-size: 62.5%;
    min-width: 300px;
  }

`;
