import Header from "@/components/Header";
import IconLabel from "@/components/IconLabel";
import Increment from "@/components/Increment";
import { useCart } from '@/context/Cart/CartContext';
import { ThumbnailSmall } from "@/styles/genericStyles";
import { Address, Card, Container, Info, Total } from "@/styles/pages/Cart/styles";
import PrismicDOM from 'prismic-dom';
import { useEffect } from "react";
import { FiCalendar, FiMapPin, FiTruck } from "react-icons/fi";

export default function Cart() {
  const {cart, address } = useCart();

  useEffect(() => {
    console.log(cart);
  }, [cart])
  // setCart({ type: 'ADD', payload: item });
  // setCart({ type: 'REMOVE', payload: product.id });

  return (
    <>
      <Header />
      <Container>
        {cart.map(item => {
          let quantity = parseInt(item.quantity);
          return (
            <Card key={item.product.id}>
              <section>
              <ThumbnailSmall src={item.product.data.thumbnail.url} alt=""/>
              <Info>
                <span>{PrismicDOM.RichText.asText(item.product.data.name)}</span>
                <Address>
                  <FiCalendar size={15}/>
                  <p>Chegará em </p>
                </Address>
                <div>
                  <span>R$ {quantity * parseInt(item.product.data.price)}</span>
                </div>
              </Info>
              </section>
              <Increment initial={quantity}/>
            </Card>
          )}
        )}
        <Card>
          <Total>
            <p>Endereço de entrega: </p>
            <IconLabel icon={(<FiMapPin size={15}/>)} label={(address.street, address.neighborhood, address.city - address.state)} />

              {/* <button>
                <FiEdit2 size={15} />
              </button> */}
            <br/>
            <p>Valor do frete: </p>
            <Address>
              <FiTruck size={15} />
              <p>R$ 36,90</p>
            </Address>
            <br/>
            <p>Total: </p>
            <Address>
              <span>R$ 460,90</span>
            </Address>
          </Total>
        </Card>
      </Container>
    </>
  );
}
