import React, { memo, useState } from 'react';

interface IncrementProps {
  // title: string;
  initial?: number;
}

const Increment: React.FC<IncrementProps> = ({initial}) => {
  const [quantity, setQuantity] = useState(initial.toString());

  function increment() {
    let sum = parseInt(quantity) + 1;
    setQuantity(sum.toString());
  }

  function decrement() {
    let subtract = parseInt(quantity) - parseInt('1');
    setQuantity(subtract.toString());
  }

  return (
    <>
    <button type="button" onClick={() => increment()}>+</button>
    <p>{quantity}</p>
    <button type="button" onClick={() => decrement()}>-</button>
    </>
  );
}

export default memo(Increment)
